mapper.select:
SELECT @rownum:=@rownum+1 as row_no, p.post_no, p.post_asc, p.post_title, p.post_contents, date_format(POST_DATE, '%Y-%m-%d') post_date, p.post_origin_no, p.board_no, p.member_no, p.member_name FROM board b, post p, member m, (select @rownum:=0) row_reset WHERE b.board_no = p.board_no AND m.member_no = p.member_no AND b.board_no = #{boardNo}
AND p.post_title like concat ('%',#{search.searchKeyword},'%')
AND p.member_name like concat ('%',#{search.searchKeyword},'%')
ORDER BY p.post_asc ASC LIMIT #{limit} OFFSET #{offset}

SELECT @rownum:=@rownum+1 as row_no, p.post_no, p.post_asc, p.post_title, p.post_contents, date_format(p.POST_DATE, '%Y-%m-%d') post_date, p.post_origin_no, p.board_no, p.member_no, p.member_name FROM board b, post p, member m, (select @rownum:=0) row_reset WHERE b.board_no = p.board_no AND m.member_no = p.member_no AND b.board_no = #{boardNo}
AND p.post_title like concat ('%',#{search.searchKeyword},'%')
AND p.member_name like concat ('%',#{search.searchKeyword},'%')
ORDER BY p.post_asc ASC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE board

	board_title = #{boardTitle},
	board_type = #{boardType},
	board_category = #{boardCategory},
	board_email = #{boardEmail} 

WHERE board_no = #{boardNo : NUMERIC}

UPDATE post

	post_title = #{postTitle},
	post_Contents = #{postContents},
	post_origin_no = #{postOriginNo}

WHERE post_no = #{postNo}

UPDATE BOARD_OPTION
OPTION_NOTICE = #{optionNotice}, OPTION_COMMENT = #{optionComment}, OPTION_SECRET = #{optionSecret}, OPTION_ANSWER = #{optionAnswer}, OPTION_CAPTCHA = #{optionCaptcha}, OPTION_MASS = #{optionMass}, OPTION_ORDER = #{optionOrder}, OPTION_ADDINFO = #{optionAddinfo}, OPTION_POPUP = #{optionPopup}
WHERE board_no = #{optionBoard : NUMERIC}
